mapper.insert:
INSERT INTO INSTRUCTOR (
    INST_ID, PASSWORD, NAME, PHONE, EMAIL, BIRTH, PROFILE, REGIP, APPROVAL, ACTIVATION, REGDATE
) VALUES (
    #{instId}, #{password}, #{name}, #{phone, jdbcType=VARCHAR}, #{email}, #{birth, jdbcType=DATE}, #{profile, jdbcType=VARCHAR}, #{regip, jdbcType=VARCHAR}, 0, 1, SYSDATE
)

mapper.select:
SELECT INST_ID
FROM INSTRUCTOR
WHERE INST_ID = #{id}
