mapper.select:
SELECT  USER_ID as id, NAME, PROFILE as img, EMAIL, PHONE, PASSWORD, INTRO
FROM    users
WHERE   user_id = #{userId}

mapper.update:
UPDATE  users
SET     PROFILE = #{imgPath}
WHERE   user_id = #{userId}

UPDATE  users
SET     NAME = #{name}
WHERE   user_id = #{userId}

UPDATE  users
SET     INTRO = #{intro}
WHERE   user_id = #{userId}

UPDATE  users
SET     EMAIL = #{email}
WHERE   user_id = #{userId}

mapper.select:
SELECT  PASSWORD
FROM    users
WHERE   user_id = #{userId}

mapper.update:
UPDATE  users
SET     PASSWORD = #{password}
WHERE   user_id = #{userId}

UPDATE USERS
SET PHONE = #{phone}
WHERE USER_ID = #{userId}

UPDATE USERS
SET ACTIVATION = 0
WHERE USER_ID = #{userId}
