mapper.insert:
INSERT INTO INSTRUCTOR (
    INST_ID, PASSWORD, NAME, PHONE, EMAIL, BIRTH, PROFILE, REGIP, APPROVAL, ACTIVATION, REGDATE
) VALUES (
    #{instId}, #{password}, #{name}, #{phone}, #{email}, #{birth}, #{profile}, #{regip}, 0, 1, SYSDATE
)

mapper.select:
SELECT INST_ID
FROM INSTRUCTOR
WHERE INST_ID = #{id}
